// React
import { useState, useEffect, useRef } from 'react'

// Tailwind
import './tailwind/tailwind.css'
import './tailwind/output.css'

// Socket
const io = window.io

// Peerjs
const Peer = windows.Peer

function App() {

    const [uid, setUid] = useState()
    const [roomId, setRoomId] = useState()

    const myVid = useRef()
    const [myStream, setMyStream] = useState()

    const usrsVid = []

    const socket = io('/')
    const myPeer = new Peer(undefined, {
        host: '/',
        port: '5001'
    })

    // Init myVid
    useEffect(() => {
        const myVideoSource = document.createElement('video')
        myVideoSource.muted = true
        navigator.mediaDevices.getUserMedia({
            video: true,
            audio: true
        }).then(stream => {
            setMyStream(stream)
            myVid.current.srcObject = stream
        })
    }, [])

    useEffect(() => {
        // get UID and ROOMID
        if (localStorage.getItem('roomId') != null && localStorage.getItem('uid') != null) {
            setUid(localStorage.getItem('uid'));
            setRoomId(localStorage.getItem('roomId'))
        } else {
            // Fetch from server
        }

        if (uid == null || roomId == null) console.log('Error Room / UID', uid, roomId)

    }, [])

    return (
        <div className='h-full flex items-center justify-center'>
            <div id="video-grid" className='grid grid-cols-2 gap-4'>
            </div>
        </div>
    );
}

export default App;
